import React from "react";

export default function Question(props) {

  function buttonColor(index){
    if(props.choosen_answer === props.all_ans[index]){
        if (props.check && props.chosenAns !== props.correctAns) {
        return {background: "#f29ba7", borderColor: "red"}
        } else if (props.check && props.chosenAns == props.correctAns) {
        return {background: "#7bc983", borderColor: "#FFD700"}
        } else {
        return {background: "lightblue", borderColor: "#034a16"}
        }
    }
    if (props.check && props.randAnswers[index] == props.correctAns) {
        return {background: "#c8f7c6"}
    }
    
  }
  return (
    <div>
      <h4>{props.question}</h4>
      <div className="options">
        <button
          onClick={props.check ? null : props.chooseAns}
          id={props.id}
          value={props.check ? null : props.all_ans[0]}
        >
          {props.all_ans[0]}
        </button>
        <button
          onClick={props.check ? null : props.chooseAns}
          id={props.id}
          value={props.all_ans[1]}
        >
          {props.all_ans[1]}
        </button>
        <button
          onClick={props.check ? null : props.chooseAns}
          id={props.id}
          value={props.all_ans[2]}
        >
          {props.all_ans[2]}
        </button>
        <button
          onClick={props.chooseAns}
          id={props.id}
          value={props.all_ans[3]}
        >
          {props.all_ans[3]}
        </button>
      </div>
    </div>
  );
}
