import React from "react";
import CheckAll from "./CheckAll";
import Question from "./Question";

export default function Quiz() {
  const [questionData, setQuestionData] = React.useState(() => {
    let defaultInfo = [];
    for (let n = 0; n < 5; n++) {
      defaultInfo.push({
        question: "Loading...",
        correct_ans: "",
        all_ans: ["...", "...", "...", "..."],
        id: Math.floor(Math.random() * 999999999),
        choosen_answer: "...",
      });
    }
    return defaultInfo;
  });

  const [correctCounter, setCorrectCounter] = React.useState({
    totalCorrect: 0,
    currentCorrect: 0,
    check: false,
    reset: 0,
  });

  React.useEffect(() => {
    fetch(
      "https://opentdb.com/api.php?amount=5&category=18&difficulty=easy&type=multiple"
    )
      .then((data) => data.json())
      .then((responce) =>
        setQuestionData(
          responce.results.map((q) => ({
            question: q.question,
            correct_ans: q.correct_answer,
            all_ans: [...q.incorrect_answers, q.correct_answer].sort(
              () => 0.5 - Math.random()
            ),
            id: Math.floor(Math.random() * 999999999),
            choosen_answer: "...",
          }))
        )
      );
  }, [correctCounter.reset]);

  function chooseAns(event) {
    console.log(event);
    setQuestionData((prevQuestionData) =>
      prevQuestionData.map((currentData) => {
        if (currentData.id == event.target.id) {
          return { ...currentData, choosen_answer: event.target.value };
        } else {
          return currentData;
        }
      })
    );
  }

  function checkCorrect() {
    let correct = 0;
    for (let n = 0; n < questionData.length; n++) {
      if (questionData[n].correct_answer == questionData[n].chosen_answer) {
        correct += 1;
      }
    }
    setCorrectCounter((prevCorrectCounter) => ({
      totalCorrect: prevCorrectCounter.totalCorrect + 1,
      currentCorrect: correct,
      check: true,
      reset: prevCorrectCounter,
    }));
    console.log(questionData);
  }

  const questionDatajsx = questionData.map((q) => (
    <Question
      question={q.question}
      correct_ans={q.correct_ans}
      all_ans={q.all_ans}
      key={q.id}
      id={q.id}
      choosen_answer={q.choosen_answer}
      chooseAns={chooseAns}
      check={correctCounter.check}
    />
  ));

  return (
    <div>
      {questionDatajsx}
      <CheckAll correctCounter={correctCounter} handleClick={checkCorrect} />
    </div>
  );
}
