import "./App.css";
import React, { useEffect } from "react";
import Start from "./components/Start";
import Quiz from "./components/Quiz";

function App() {
  const [isFirstTime, setFirstTime] = React.useState(false);
  const [questionData, setQuestionData] = React.useState(() => {
    let defaultInfo = [];
    for (let n = 0; n < 4; n++) {
      defaultInfo.push({
        question: "Loading...",
        correct_ans: "",
        all_ans: ["...", "...", "...", "..."],
        id: Math.floor(Math.random() * 999999999),
        choosen_answer: "...",
      });
    }
    return defaultInfo;
  });

  React.useEffect(() => {
    fetch(
      "https://opentdb.com/api.php?amount=5&category=18&difficulty=easy&type=multiple"
    )
      .then((data) => data.json())
      .then((responce) =>
        setQuestionData(
          responce.result.map((q) => ({
            question: q.question,
            correct_ans: q.correct_answer,
            all_ans: [...q.incorrect_answers, q.correct_answer].sort(
              () => 0.5 - Math.random()
            ),
            id: Math.floor(Math.random() * 999999999),
            choosen_answer: "...",
          }))
        )
      );
  }, isFirstTime);

  function startQuiz() {
    setFirstTime((prevFirst) => !prevFirst);
  }


  return (
    <div>
      {isFirstTime ? (
        <Quiz questionData={questionData} />
      ) : (
        <Start handleClick={startQuiz} />
      )}
    </div>
  );
}

export default App;
